mapper.select:
SELECT D.DIGGING_ID AS DIGGING_ID, D.WRITER AS WRITER, D.SUBJECT AS SUBJECT, D.CONTENT AS CONTENT, D.READ_CNT AS READ_CNT, D.DIGGING_TOPIC AS DIGGING_TOPIC, D.ENROLL_DT AS ENROLL_DT, D.REPLY_CNT AS REPLY_CNT, D.THUMBS_UP AS THUMBS_UP, D.FILE AS FILE, D.VIDEO_YN AS VIDEO_YN, D.VIDEO_ID AS VIDEO_ID, D.DIG AS DIG, U.PROFILE AS PROFILE FROM DIGGING D JOIN USER U ON D.WRITER = U.USER_ID WHERE DIGGING_TOPIC = #{diggingTopic}
AND DIG = #{dig}
ORDER BY
READ_CNT DESC
THUMBS_UP DESC
DIGGING_ID DESC
DIGGING_ID DESC
LIMIT #{startDiggingIdx}, #{onePageViewCnt}

mapper.update:
UPDATE DIGGING
SUBJECT = #{subject},
CONTENT = #{content},
file = #{file},
DIGGING_TOPIC = #{diggingTopic}
WHERE DIGGING_ID = #{diggingId}
