mapper.select:
SELECT * FROM SHOP S JOIN CART C ON S.PRODUCT_CD =C.PRODUCT_CD AND S.PRODUCT_CD IN
#{productCd}
ORDER BY C.ENROLL_DT DESC

mapper.update:
UPDATE 
		SHOP
SET
		STOCK = STOCK - #{orderQty}
WHERE
		PRODUCT_CD = #{productCd}

update.foreach:
UPDATE 
	SHOP
SET 
	STOCK = STOCK - #{productMapList.orderQty}
WHERE
	PRODUCT_CD = #{productMapList.productCd}
